CREATE OR REPLACE FUNCTION mprev_val_forma_ing_camp (nr_seq_campanha_p bigint, ie_forma_ingresso_p text, nr_seq_atual_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(10);
qt_forma_w	bigint;
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar a possível existência de uma forma de ingresso igual, para uma mesma campanha, a que está tentando inserir.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN
	if (nr_seq_campanha_p IS NOT NULL AND nr_seq_campanha_p::text <> '') and (ie_forma_ingresso_p IS NOT NULL AND ie_forma_ingresso_p::text <> '') then
		select	count(1)
		into STRICT	qt_forma_w
		from	mprev_camp_forma_ingresso
		where	nr_seq_campanha = nr_seq_campanha_p
		and	ie_forma_ingresso = ie_forma_ingresso_p
		and	nr_seq_atual_p <> nr_sequencia;

		if (qt_forma_w > 0) then
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(247879);
		else
			ds_retorno_w := 'S';
		end if;
	end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
