CREATE OR REPLACE FUNCTION mp_obter_dono_processo (nr_seq_processo_p bigint, dt_referencia_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* Opcao 
C - Codigo 
N - Nome 
*/
 
 
ds_retorno_w	varchar(80)	:= null;
cd_pessoa_w	varchar(10);
nm_pessoa_w	varchar(80)	:= null;


BEGIN 
 
if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') then 
 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_w 
	from	mp_dono 
	where	nr_seq_proc = nr_seq_processo_p 
	and	coalesce(dt_fim::text, '') = '' 
	and	dt_inicio <= fim_dia(dt_referencia_p);
 
	if (cd_pessoa_w IS NOT NULL AND cd_pessoa_w::text <> '') then 
		nm_pessoa_w := SUBSTR(OBTER_NOME_PF(cd_pessoa_w), 0, 80);
	end if;
 
	if (ie_opcao_p = 'C') then 
		ds_retorno_w	:= cd_pessoa_w;
	else 
		ds_retorno_w	:= nm_pessoa_w;
	end if;
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
