SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION mp_obter_se_conexao_destino (nr_seq_proc_obj_p bigint) RETURNS bigint AS $body$
DECLARE


ie_retorno_w		bigint	:= null;
nr_seq_fluxo_w		bigint;
nr_seq_obj_destino_w	bigint;
qt_registro_w		bigint;

/* Retorna a sequencia da atividade destino */

BEGIN

if (nr_seq_proc_obj_p IS NOT NULL AND nr_seq_proc_obj_p::text <> '') then
	/* Obter fluxo */

	select	max(nr_sequencia)
	into STRICT	nr_seq_fluxo_w
	from	mp_processo_objeto
	where	nr_seq_obj_origem	= nr_seq_proc_obj_p;

	if (nr_seq_fluxo_w IS NOT NULL AND nr_seq_fluxo_w::text <> '') then

		/* Obter se objeto destino Ã© uma referencia */

		select	max(nr_seq_obj_destino)
		into STRICT	nr_seq_obj_destino_w
		from	mp_processo_objeto
		where	nr_sequencia	= nr_seq_fluxo_w;

		if (nr_seq_obj_destino_w IS NOT NULL AND nr_seq_obj_destino_w::text <> '') then
			select	count(*)
			into STRICT	qt_registro_w
			from	mp_proc_obj_ref
			where	nr_seq_proc_obj_destino	= nr_seq_obj_destino_w;

			if (qt_registro_w > 0) then
				ie_retorno_w	:= nr_seq_obj_destino_w;
			end if;
		end if;
	end if;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
