SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION mp_obter_se_mostra_processo ( nr_seq_processo_p bigint, ie_visualiza_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
ie_visualiza_p - Parâmetro [3] 
 
T - Visualiza todos os processos 
D - Somente os processos que é dono 
E - Somente os processos que está na equipe ou que é dono 
 
*/
 
 
ie_retorno_w		varchar(1)	:= 'N';
nr_seq_proc_filho_w	bigint;
cd_dono_w		varchar(10);
cd_pessoa_fisica_w	varchar(10);
ie_equipe_w		varchar(1);

c01 CURSOR FOR 
SELECT	nr_sequencia 
from	mp_processo 
where	nr_seq_superior	= nr_seq_processo_p;


BEGIN 
 
if (ie_visualiza_p = 'N') then 
	ie_retorno_w	:= 'N';
elsif (ie_visualiza_p	= 'T') then 
	ie_retorno_w	:= 'S';
else 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from	usuario 
	where	nm_usuario	= nm_usuario_p;
 
	/* Ver se processo do parametro */
 
	if (ie_visualiza_p = 'D') then 
		cd_dono_w		:= substr(mp_obter_dono_processo(nr_seq_processo_p,clock_timestamp(),'C'),1,10);
 
		if (cd_dono_w = cd_pessoa_fisica_w) then 
			ie_retorno_w	:= 'S';
		end if;
 
	elsif (ie_visualiza_p = 'E') then 
		ie_equipe_w		:= substr(mp_obter_se_partic_processo(nr_seq_processo_p,nm_usuario_p),1,1);
 
		if (ie_equipe_w in ('S','D')) then 
			ie_retorno_w	:= 'S';
		end if;
	end if;
 
	if (ie_retorno_w = 'N') then 
 
		open c01;
		loop 
		fetch c01 into 
			nr_seq_proc_filho_w;
		EXIT WHEN NOT FOUND or ie_retorno_w = 'S';  /* apply on c01 */
			if (ie_visualiza_p = 'D') then 
	 
				cd_dono_w		:= substr(mp_obter_dono_processo(nr_seq_proc_filho_w,clock_timestamp(),'C'),1,10);
	 
				if (cd_dono_w = cd_pessoa_fisica_w) then 
					ie_retorno_w	:= 'S';
				else 
					ie_retorno_w	:= mp_obter_se_mostra_processo(nr_seq_proc_filho_w,ie_visualiza_p,nm_usuario_p);
				end if;
 
			elsif (ie_visualiza_p = 'E') then 
	 
				ie_equipe_w	:= substr(mp_obter_se_partic_processo(nr_seq_proc_filho_w,nm_usuario_p),1,1);
 
				if (ie_equipe_w in ('S','D')) then 
					ie_retorno_w	:= 'S';
				else 
					ie_retorno_w	:= mp_obter_se_mostra_processo(nr_seq_proc_filho_w,ie_visualiza_p,nm_usuario_p);
				end if;
			end if;
		end loop;
		close c01;
 
	end if;
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
