SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION mp_obter_se_partic_processo (nr_seq_processo_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
S - Participante 
N - Não participante 
D - Dono 
*/
 
 
ie_retorno_w		varchar(1)	:= 'N';
cd_pessoa_usuario_w	varchar(10);
cd_pessoa_dono_w	varchar(10);
cd_cargo_w		bigint;
cd_setor_atendimento_w	integer;
qt_registro_w		integer;


BEGIN 
 
if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') then 
	/* Verificar se dono */
 
	select	max(cd_pessoa_fisica), 
		max(cd_setor_atendimento) 
	into STRICT	cd_pessoa_usuario_w, 
		cd_setor_atendimento_w 
	from	usuario 
	where	nm_usuario	= nm_usuario_p;
 
	cd_pessoa_dono_w	:= mp_obter_dono_processo(nr_seq_processo_p,clock_timestamp(),'C');
 
	if (cd_pessoa_usuario_w = cd_pessoa_dono_w) then 
		ie_retorno_w	:= 'D';
	end if;	
 
	/* Se não for dono, ve se é participante da equipe */
 
	select	max(cd_cargo) 
	into STRICT	cd_cargo_w 
	from	pessoa_fisica 
	where	cd_pessoa_fisica	= cd_pessoa_usuario_w;
 
	select	count(*) 
	into STRICT	qt_registro_w 
	from	mp_proc_regra_equip 
	where	nr_seq_proc	= nr_seq_processo_p 
	and	coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_w,0))	= coalesce(cd_setor_atendimento_w,0) 
	and	coalesce(cd_cargo,coalesce(cd_cargo_w,0))				= coalesce(cd_cargo_w,0) 
	and	coalesce(cd_pessoa_fisica,coalesce(cd_pessoa_usuario_w,0))	= coalesce(cd_pessoa_usuario_w,0);
 
	if (qt_registro_w > 0) then 
		ie_retorno_w	:= 'S';
	end if;
end if;
 
return ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
