CREATE OR REPLACE FUNCTION mp_obter_se_processo_relac (nr_seq_processo_p bigint, nr_seq_processo_ref_p bigint, ie_tipo_relac_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);
nr_seq_origem_w	bigint;

/* IE_TIPO_RELAC_P

F - Processo filho (versao anterior)
P - Processo pai (versao atual)
T - Todos

*/
BEGIN

ie_retorno_w	:= 'N';

select	max(nr_seq_origem)
into STRICT	nr_seq_origem_w
from	mp_processo
where	nr_sequencia	= nr_seq_processo_ref_p;

if (nr_seq_processo_p = nr_seq_processo_ref_p) then
	ie_retorno_w	:= 'S';
end if;

if (ie_tipo_relac_p in ('F','T')) then

	while(nr_seq_origem_w IS NOT NULL AND nr_seq_origem_w::text <> '') and (ie_retorno_w = 'N') loop

		if (nr_seq_origem_w = nr_seq_processo_p) then
			ie_retorno_w	:= 'S';
		end if;

		select	nr_seq_origem
		into STRICT	nr_seq_origem_w
		from	mp_processo
		where	nr_sequencia	= nr_seq_origem_w;
	end loop;
end if;

if (ie_tipo_relac_p in ('P','T')) then

	/* Se n√£o achou filho, procurar por pai */

	if (ie_retorno_w = 'N') then
		select	max(nr_seq_origem)
		into STRICT	nr_seq_origem_w
		from	mp_processo
		where	nr_sequencia	= nr_seq_processo_p;

		while(nr_seq_origem_w IS NOT NULL AND nr_seq_origem_w::text <> '') and (ie_retorno_w = 'N') loop

			if (nr_seq_origem_w = nr_seq_processo_ref_p) then
				ie_retorno_w	:= 'S';
			end if;

			select	nr_seq_origem
			into STRICT	nr_seq_origem_w
			from	mp_processo
			where	nr_sequencia	= nr_seq_origem_w;
		end loop;
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
