SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION mp_obter_tipo_grupo_obj (nr_seq_proc_objeto_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_grupo_w		varchar(2)	:= null;
nr_seq_objeto_w		bigint;
nr_seq_grupo_w		bigint;


BEGIN

if (nr_seq_proc_objeto_p IS NOT NULL AND nr_seq_proc_objeto_p::text <> '') then
	select	max(nr_seq_objeto)
	into STRICT	nr_seq_objeto_w
	from	mp_processo_objeto
	where	nr_sequencia	= nr_seq_proc_objeto_p;

	if (nr_seq_objeto_w IS NOT NULL AND nr_seq_objeto_w::text <> '') then
		select	max(nr_seq_grupo)
		into STRICT	nr_seq_grupo_w
		from	mp_objeto
		where	nr_sequencia	= nr_seq_objeto_w;

		if (nr_seq_grupo_w IS NOT NULL AND nr_seq_grupo_w::text <> '') then
			select	max(ie_tipo_grupo)
			into STRICT	ie_tipo_grupo_w
			from	mp_grupo_objeto
			where	nr_sequencia	= nr_seq_grupo_w;
		end if;
	end if;
end if;

return	ie_tipo_grupo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
