CREATE OR REPLACE FUNCTION mp_obter_usuarios_chave_proc (nr_seq_processo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255)	:= null;
ds_usuario_chave_w	varchar(255);

c01 CURSOR FOR
SELECT	a.ds_usuario_chave
from	mp_processo_usuario_chave a
where	a.nr_seq_processo	= nr_seq_processo_p
order by
	1;


BEGIN

if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') then
	open c01;
	loop
	fetch c01 into
		ds_usuario_chave_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
			ds_retorno_w	:= ds_retorno_w || ',' || ds_usuario_chave_w;
		else
			ds_retorno_w	:= ds_usuario_chave_w;
		end if;
		end;
	end loop;
	close c01;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
