CREATE OR REPLACE FUNCTION nfe_elimina_caractere_especial (ds_campo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000);
ds_retorno_ww	varchar(2000);
i     			integer;
k     			integer;
j				integer;
ds_car_w		varchar(001);
ds_acento_w	varchar(255)	:= 'áãàâäéèêëíìîïóõòôöúùûüçñÁÃÀÂÄÉÈÊËÍÌÎÏÓÕÒÔÖÚÙÛÜÇÑ&#ªº°<>´`*§£';
ds_novo_w	varchar(255)	:= 'aaaaaeeeeiiiiooooouuuucnAAAAAEEEEIIIIOOOOOUUUUCNe   o       ';



BEGIN

if (ds_campo_p IS NOT NULL AND ds_campo_p::text <> '') then
begin
	for i in 1..length(ds_campo_p) loop
		begin
			ds_car_w	:= substr(ds_campo_p, i, 1);
			k			:= position(ds_car_w in ds_acento_w);
			if (k = 0) then
				ds_retorno_w	:= substr(ds_retorno_w || ds_car_w,1,4000);
			else
				ds_retorno_w	:= substr(ds_retorno_w || substr(ds_novo_w,k,1),1,4000);
			end if;
		end;
	end loop;
end;
end if;
	

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	begin

	for	j in 1 .. length(ds_retorno_w) loop
		begin
		if	--not (substr(ds_retorno_w,j,1) in ('-','.',',','/','\','''')) then
			not(substr(ds_retorno_w,j,1) in ('''')) then
			ds_retorno_ww	:= ds_retorno_ww || substr(ds_retorno_w,j,1);	
		end if;
		end;
	end loop;
	end;
end if;
	
return	ds_retorno_ww;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
