CREATE OR REPLACE FUNCTION nfe_obter_pmc_material (cd_estabelecimento_p bigint, dt_periodo_p timestamp, cd_material_p bigint, ie_lista_material_p text, nr_seq_classif_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);
lista_pos_w	varchar(100);
lista_neg_w	varchar(100);
lista_neu_w	varchar(100);
preco_custo_w	double precision;
preco_total_w	double precision;



BEGIN

select 	coalesce(pr_lista_positiva,0),
	coalesce(pr_lista_negativa,0),
	coalesce(pr_lista_neutra,0)
into STRICT	lista_pos_w,
	lista_neg_w,
	lista_neu_w
from	lista_ncm
where	nr_sequencia = nr_seq_classif_p;

preco_custo_w := obter_custo_medio_material(cd_estabelecimento_p,dt_periodo_p,cd_material_p);

if (ie_lista_material_p = '0') then
	preco_total_w	:= (preco_custo_w + (preco_custo_w * lista_pos_w)/100);
elsif (ie_lista_material_p = '1') then
	preco_total_w	:= (preco_custo_w * (preco_custo_w * lista_neg_w)/100);
elsif (ie_lista_material_p = '2') then
	preco_total_w	:= (preco_custo_w * (preco_custo_w * lista_neu_w)/100);
end if;

ds_retorno_w	:= to_char(preco_total_w,'FM99990.0099');

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
