CREATE OR REPLACE FUNCTION nfe_obter_valores_impostos (nr_item_nf_p bigint, nr_seq_nf_p bigint, ie_tipo_imposto_p text, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
vBC_w		varchar(100);
pImposto_w	varchar(100);
vImposto_w	varchar(100);


BEGIN

select	to_char(cast(a.vl_base_calculo as double precision) ,'FM99990.0099') vBC,
	to_char(cast(a.tx_tributo as double precision),'FM99990.0099') pImposto,
	to_char(cast(a.vl_tributo as double precision),'FM99990.0099') vImposto
into STRICT	vBc_w,
	pImposto_w,
	vImposto_w
from	nota_fiscal_item_trib a,
	tributo b
where	a.cd_tributo = b.cd_tributo
and	b.ie_tipo_tributo = ie_tipo_imposto_p
and	a.nr_sequencia = nr_seq_nf_p
and	a.nr_item_nf  = nr_item_nf_p;

if (ie_tipo_p = 'BC') then
	ds_retorno_w := vBc_w;
elsif (ie_tipo_p = 'PIMP') then
	ds_retorno_w := pImposto_w;
elsif (ie_tipo_p = 'VIMP') then
	ds_retorno_w := vImposto_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
