CREATE OR REPLACE FUNCTION nfe_obter_valores_totais_num ( nr_seq_nf_p bigint, ie_tipo_tributo_p text, ie_tipo_soma_p text) RETURNS bigint AS $body$
DECLARE


/* ALTERAR A FUNCTION NFE_OBTER_VALORES_TOTAIS */

vl_retorno_w	double precision;
vl_base_w	double precision;
vl_tributo_w	double precision;



BEGIN

select	sum(a.vl_base_calculo),
	sum(a.vl_tributo)
into STRICT	vl_base_w,
	vl_tributo_w
from	nota_fiscal_item_trib a,
	tributo d
where	a.cd_tributo = d.cd_tributo
and	a.nr_sequencia = nr_seq_nf_p
and 	d.ie_tipo_tributo = ie_tipo_tributo_p
and	coalesce(a.ie_rateio,'N') = 'N';

if (ie_tipo_soma_p = 'BC') then
	vl_retorno_w := vl_base_w;
elsif (ie_tipo_soma_p = 'VL') then
	vl_retorno_w := vl_tributo_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
