SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION nfse_obter_dados_dsfnet ( nr_seq_nota_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



qt_resultado_nota_w		bigint;
nr_inscricao_municipal_w	varchar(11);
retorno_w			varchar(11);


BEGIN

select	count(*)
into STRICT	qt_resultado_nota_w
from	nota_fiscal
where	nr_sequencia =  nr_seq_nota_p;

if (qt_resultado_nota_w > 0) then

	if (ie_opcao_p = 'IM') then

		select	CASE WHEN p.cd_municipio_ibge='221100' THEN lpad(nr_inscricao_municipal,7,'0') WHEN p.cd_municipio_ibge='150140' THEN lpad(nr_inscricao_municipal,7,'0') WHEN p.cd_municipio_ibge='350950' THEN lpad(nr_inscricao_municipal,9,'0') WHEN p.cd_municipio_ibge='500270' THEN lpad(nr_inscricao_municipal,11,'0') WHEN p.cd_municipio_ibge='317020' THEN lpad(nr_inscricao_municipal,8,'0') WHEN p.cd_municipio_ibge='330350' THEN lpad(nr_inscricao_municipal,6,'0') WHEN p.cd_municipio_ibge='211130' THEN lpad(nr_inscricao_municipal,11,'0') WHEN p.cd_municipio_ibge='355220' THEN lpad(nr_inscricao_municipal,9,'0') END
		into STRICT	nr_inscricao_municipal_w
		from	pessoa_juridica p,
			nota_fiscal n
		where	n.nr_sequencia = nr_seq_nota_p
		and	n.cd_cgc_emitente = p.cd_cgc;

		retorno_w:= nr_inscricao_municipal_w;

	end if;

end if;

return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
