SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION nfse_ops_compl_pf_pj ( nr_seq_mensalidade_p text, cd_cgc_p text) RETURNS varchar AS $body$
DECLARE

				 
ie_endereco_boleto_w	varchar(5);
nr_seq_pagador_w	bigint;
ie_comp_pj_w		bigint;
ds_retorno_w		varchar(2);
				

BEGIN 
 
				 
select	nr_seq_pagador 
into STRICT	nr_seq_pagador_w 
from	pls_mensalidade 
where	nr_sequencia = nr_seq_mensalidade_p;
 
 
select	ie_endereco_boleto 
into STRICT	ie_endereco_boleto_w 
from	pls_contrato_pagador 
where	nr_sequencia = nr_seq_pagador_w;
 
if	((ie_endereco_boleto_w = 'PFR') or (ie_endereco_boleto_w = 'PJC')) then 
	ds_retorno_w := '1';
elsif (ie_endereco_boleto_w = 'PFC') then 
	ds_retorno_w := '2';
elsif (ie_endereco_boleto_w = 'PJF') then 
	 
	 
	select	count(*) 
	into STRICT	ie_comp_pj_w 
	from	pessoa_juridica_compl 
	where	cd_cgc	= cd_cgc_p 
	and	ie_tipo_complemento 	= '2';
	 
	if (ie_comp_pj_w > 0) then 
		ds_retorno_w := '2';
	else 
		ds_retorno_w := '3';
	end if;
 
elsif (ie_endereco_boleto_w = 'PJA') then 
	ds_retorno_w := '6';
elsif (ie_endereco_boleto_w = 'PJ') then 
	ds_retorno_w := '9';
elsif (ie_endereco_boleto_w = 'PFV') then 
	ds_retorno_w := '3';
elsif (ie_endereco_boleto_w = 'PFP') then 
	ds_retorno_w := '4';
elsif (ie_endereco_boleto_w = 'PFM') then 
	ds_retorno_w := '5';
elsif (ie_endereco_boleto_w = 'PFJ') then 
	ds_retorno_w := '6';	
	 
end if;
 
 
return	ds_retorno_w;
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
