CREATE OR REPLACE FUNCTION nf_obter_idioma_sis (nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(10);


BEGIN

ds_retorno_w := 'BR';

if (philips_param_pck.get_nr_seq_idioma = 1) then -- Português (BR)
	ds_retorno_w := 'BR';
elsif (philips_param_pck.get_nr_seq_idioma = 2) then -- Espanhol  (MX)
	ds_retorno_w := 'MX';
elsif (philips_param_pck.get_nr_seq_idioma = 3) then -- Espanhol  (CO)
	ds_retorno_w := 'CO';
elsif (philips_param_pck.get_nr_seq_idioma = 4) then -- Espanhol  (AR)
	ds_retorno_w := 'AR';
elsif (philips_param_pck.get_nr_seq_idioma = 5) then -- Inglês    (US)
	ds_retorno_w := 'US';
end if;



return	ds_retorno_w;

end;
