CREATE OR REPLACE FUNCTION nom_obter_doenca_lista_pac (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

ie_retorno_w	varchar(2) := 'N';
qt_cd_doenca_w	integer := 0;


BEGIN

	select	coalesce(count(cd_doenca), 0)
	into STRICT	qt_cd_doenca_w
	from	lista_problema_pac
	where 	cd_pessoa_fisica = cd_pessoa_fisica_p
	and		obter_tipo_cronico_cid(cd_doenca) in ('OBSDA', 'DIABT', 'HIPAS', 'LIPDM');
	
	if (qt_cd_doenca_w <> 0) then
		ie_retorno_w := 'S';
	end if;
	
return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
