SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION nom_obter_jurisdicao_def (cd_estabelecimento_p bigint, cd_clues_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


cd_juridisicao_w	varchar(10);
cd_clues_estab_w	varchar(255);


BEGIN

if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	select	max(b.cd_jurisdicao)
	into STRICT	cd_juridisicao_w
	from	cat_jurisdicao b,
			inform_adic_usuario a
	where	a.nr_seq_jurisdicao = b.nr_sequencia
	and		a.nm_usuario_inf_adic	= nm_usuario_p;
elsif (cd_clues_p IS NOT NULL AND cd_clues_p::text <> '') and (cd_clues_p not in ('9997','9998','9999')) then
	SELECT	MAX(cd_jurisdicao)
	into STRICT	cd_juridisicao_w
	from 	cat_clues
	where 	cd_clues = cd_clues_p;
else
	select	max(b.cd_internacional)
	into STRICT	cd_clues_estab_w
	from	pessoa_juridica b,
			estabelecimento a
	where	a.cd_cgc = b.cd_cgc
	and		a.cd_estabelecimento = cd_estabelecimento_p;

	if (cd_clues_estab_w IS NOT NULL AND cd_clues_estab_w::text <> '') then
		SELECT	MAX(cd_jurisdicao)
		into STRICT	cd_juridisicao_w
		from 	cat_clues
		where 	cd_clues = cd_clues_estab_w;
	end if;
end if;

return	lpad(coalesce(cd_juridisicao_w,'88'),2,'0');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
