CREATE OR REPLACE FUNCTION nutr_obter_dt_inicio_prod ( dt_producao_p timestamp, nr_seq_turno_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w  timestamp;
hr_inicial_w  timestamp;
hr_final_w   timestamp;


BEGIN

select 	hr_inicial,
		hr_final
into STRICT 	hr_inicial_w,
		hr_final_w
from 	nut_turno_lactario
where 	nr_sequencia = nr_seq_turno_p;

if (ie_opcao_p = 'I') then
 dt_retorno_w := to_date(to_char(dt_producao_p,'dd/mm/yyyy')||' '||to_char(hr_inicial_w,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss');
else
 if (hr_final_w < hr_inicial_w) then
  dt_retorno_w := to_date(to_char(dt_producao_p+1,'dd/mm/yyyy')||' ' || to_char(hr_final_w,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss');
 else
  dt_retorno_w := to_date(to_char(dt_producao_p,'dd/mm/yyyy')||' ' || to_char(hr_final_w,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss');
 end if;
end if;

return dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
