CREATE OR REPLACE FUNCTION nut_cor_card_ordem_prod ( nr_seq_ordem_card_p bigint) RETURNS varchar AS $body$
DECLARE


qt_valida_w	bigint := 0;
ds_cor_w	varchar(15);


BEGIN

if (nr_seq_ordem_card_p IS NOT NULL AND nr_seq_ordem_card_p::text <> '') then

	    select    count(prod_card.nr_sequencia)
        	    into STRICT 	 qt_valida_w
	    from      nut_ordem_prod_card    prod_card,
                                 nut_ordem_prod             prod
	    where   prod_card.nr_seq_ordem           = prod.nr_sequencia
	    and       prod_card.nr_seq_cardapio      = nr_seq_ordem_card_p;

                   if (qt_valida_w > 0) then
	                ds_cor_w :=  obter_tasy_obter_cor(7844,obter_perfil_ativo,wheb_usuario_pck.get_cd_estabelecimento,'T');
	  end if;
end if;

return	ds_cor_w;

end;
$body$
