CREATE OR REPLACE FUNCTION nut_obter_custo_cardapio (nr_seq_receita_p bigint, nr_seq_comp_p bigint, nr_seq_cardapio_p bigint, qt_componente_p bigint ) RETURNS bigint AS $body$
DECLARE

qt_conversao_w		double precision;
vl_custo_w		double precision;
vl_custo_total_w	double precision;
cd_material_w		integer;		
cd_estabelecimento_w	smallint;

BEGIN
if (nr_seq_cardapio_p IS NOT NULL AND nr_seq_cardapio_p::text <> '') THEN
	vl_custo_w := 0;
ELSE
	BEGIN
		SELECT  coalesce(max(c.qt_conversao),0),
			coalesce(max(c.cd_material),0)
			INTO STRICT	qt_conversao_w,
			cd_material_w
		FROM   	nut_receita a,
			nut_receita_comp b,
			nut_genero_alim c
		WHERE   a.nr_sequencia = b.nr_seq_receita
		AND	c.nr_sequencia = b.nr_seq_gen_alim	
		AND	a.nr_sequencia = nr_seq_receita_p
		AND	b.nr_sequencia = nr_seq_comp_p;
		SELECT coalesce(max(c.vl_custo),0)
		INTO STRICT	vl_custo_w
		FROM	custo_mensal_material c
		WHERE 	c.cd_estabelecimento	= cd_estabelecimento_w
		AND	c.cd_material		= cd_material_w
		AND	c.ie_tipo_custo		= 'R'
		AND	c.dt_referencia		=
				(SELECT max(dt_referencia)
				FROM	custo_mensal_material c
				WHERE 	c.cd_estabelecimento	= cd_estabelecimento_w
				AND	c.cd_material		= cd_material_w
				AND	c.ie_tipo_custo		= 'R');
		if (vl_custo_w = 0) THEN
			SELECT	coalesce(Obter_Valor_Ultima_Compra(cd_estabelecimento_w,90,cd_material_w,NULL,'N'),0)
			INTO STRICT	vl_custo_w
			;
		end if;
		vl_custo_total_w	:= vl_custo_w * qt_componente_p / qt_conversao_w;
		
		exception
			when division_by_zero then
				vl_custo_total_w := 0;

	END;
end if;
return	vl_custo_total_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
