CREATE OR REPLACE FUNCTION nut_obter_custo_material (cd_material_p bigint, qt_material_p bigint, cd_unid_med_genero_p text default null) RETURNS bigint AS $body$
DECLARE


cd_unidade_medida_estoque_w	varchar(30);
qt_dose_convertida_w		double precision;
vl_custo_w					double precision;
cd_estabelecimento_w		smallint;
qt_custo_total_w			double precision;
ie_resultParametro_w            varchar(1);
cd_unid_med_genero_w		varchar(30);


BEGIN
cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;
ie_resultParametro_w := obter_param_usuario(1003, 112, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_resultParametro_w);

cd_unid_med_genero_w := coalesce(cd_unid_med_genero_p,'G');

qt_dose_convertida_w := 0;
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select 	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_w,'UME'),1,30) cd_unidade_medida_consumo
	into STRICT	cd_unidade_medida_estoque_w
	from	material
	where 	cd_material = cd_material_p;

	if (upper(cd_unidade_medida_estoque_w) <> cd_unid_med_genero_w) then
		qt_dose_convertida_w := Obter_dose_convertida(cd_material_p,qt_material_p,cd_unid_med_genero_w,cd_unidade_medida_estoque_w);
	else
		qt_dose_convertida_w := qt_material_p;
	end if;

	if (ie_resultParametro_w = 'M') then
	   vl_custo_w 	  := obter_custo_medio_material(cd_estabelecimento_w,TRUNC(clock_timestamp(),'mm'),cd_material_p);
	else
	   vl_custo_w 	  := nut_obter_custo_ult_compra_gen(cd_estabelecimento_w,cd_material_p);
	end if;

	qt_custo_total_w := qt_dose_convertida_w * vl_custo_w;

end if;

return	qt_custo_total_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
