CREATE OR REPLACE FUNCTION nut_obter_custo_receita (nr_seq_receita_p bigint) RETURNS bigint AS $body$
DECLARE


vl_custo_total_w	double precision := 0;


BEGIN

if (nr_seq_receita_p IS NOT NULL AND nr_seq_receita_p::text <> '') then

	select	sum(coalesce(nut_obter_custo_rec_comp(nr_sequencia),0))
	into STRICT	vl_custo_total_w
	from	nut_receita_comp
	where	nr_seq_receita = nr_seq_receita_p;

end if;

return	vl_custo_total_w;

end;
$body$
