SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION nut_obter_custo_rec_comp (nr_seq_receita_comp_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sequencia_comp_w		bigint;
qt_componente_w			double precision;
qt_conversao_w			double precision;
vl_custo_w			double precision;
vl_custo_total_w		double precision := 0;
cd_material_w			integer;
cd_estabelecimento_w		integer;
nr_seq_gen_alim_w               bigint;
qt_dias_ult_compra_w		bigint;
nr_seq_receita_w		bigint;


BEGIN

SELECT  max(nr_seq_receita),
	max(nr_seq_gen_alim),
	max(qt_componente)
into STRICT	nr_seq_receita_w,
	nr_seq_gen_alim_w,
	qt_componente_w
FROM	nut_receita_comp
WHERE	nr_sequencia = nr_seq_receita_comp_p;

select	coalesce(max(cd_estabelecimento),0)
into STRICT	cd_estabelecimento_w
from	nut_receita
where	nr_sequencia	= nr_seq_receita_w;

if (cd_estabelecimento_w > 0) then

	qt_dias_ult_compra_w := obter_valor_param_usuario(912,27,obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, cd_estabelecimento_w);

	select 	max(cd_material),
		coalesce(max(qt_conversao),1)
	into STRICT	cd_material_w,
		qt_conversao_w
	from	nut_genero_alim
	where 	nr_sequencia = nr_seq_gen_alim_w;

	-- Retirado na OS 642832 - 02/10/2013
	/*select	nvl(max(vl_custo),0)
	into	vl_custo_w
	from	(
	select  a.vl_custo vl_custo, dt_referencia , count(*) over (order by dt_referencia desc) linha
	from	custo_mensal_material a
	where 	a.cd_estabelecimento	= cd_estabelecimento_w
	and	a.cd_material		= cd_material_w
	and	a.ie_tipo_custo		= 'R'
	order by dt_referencia desc)
	where		linha = 1;*/
	select	coalesce(obter_valor_ultima_compra(cd_estabelecimento_w,coalesce(qt_dias_ult_compra_w,90),cd_material_w,null,'N'),0)
	into STRICT	vl_custo_w
	;

	begin
	vl_custo_total_w := vl_custo_w * qt_componente_w / qt_conversao_w;
	exception
	when others then
		vl_custo_total_w := 0;
	end;
end if;

return	vl_custo_total_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
