CREATE OR REPLACE FUNCTION nut_obter_custo_ult_compra_gen ( cd_estabelecimento_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE

cd_material_estoque_w    bigint;
qt_custo_result_w	 double precision;

BEGIN

  if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select max(cd_material_estoque)
	into STRICT cd_material_estoque_w
	from material
	where cd_material = cd_material_p;

	select coalesce(max(vl_preco_ult_compra), 0)
	into STRICT qt_custo_result_w
	from saldo_estoque
	where cd_estabelecimento = cd_estabelecimento_p
	and cd_material = cd_material_estoque_w
	and dt_mesano_referencia = ( SELECT max(dt_mesano_referencia)
				     from saldo_estoque
				     where cd_estabelecimento = cd_estabelecimento_p
				     and cd_material = cd_material_estoque_w);

  end if;
return	qt_custo_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
