CREATE OR REPLACE FUNCTION nut_obter_custo_unit_cardapio ( nr_seq_servico_p bigint, dt_cardapio_p timestamp) RETURNS bigint AS $body$
DECLARE



qt_pessoa_atend_real_w 	bigint;
vl_custo_w		double precision;
ds_retorno_w		double precision;



BEGIN

if (nr_seq_servico_p IS NOT NULL AND nr_seq_servico_p::text <> '') then

	SELECT	SUM(x.qt_pessoa_atend_real)
	into STRICT	qt_pessoa_atend_real_w
	FROM   	nut_cardapio_dia x
	WHERE   ((x.nr_Seq_servico = nr_seq_servico_p) OR (nr_seq_servico_p = 0))
	and 	TO_CHAR(x.dt_cardapio,'dd/mm/yyyy') = dt_cardapio_p;


	SELECT	(CASE WHEN 'S'='S',(SUM(o.vl_custo_real * 1)),SUM(o.vl_custo_real))) --(campo_mascara_virgula(DECODE('S' THEN 'S' WHEN 'S'=(SUM(o.vl_custo_real * 1)) THEN SUM(o.vl_custo_real) END ))
	into STRICT	vl_custo_w
	FROM   	nut_rec_real_comp o,
        	nut_receita_real r,
        	nut_cardapio c,
        	nut_servico s,
        	nut_cardapio_dia d
	WHERE   o.nr_seq_rec_real = r.nr_sequencia
	AND     r.nr_seq_cardapio    = c.nr_sequencia
	AND     c.nr_seq_card_dia = d.nr_sequencia
	AND     s.nr_sequencia   = d.nr_seq_servico
	AND	TO_CHAR(d.dt_cardapio,'dd/mm/yyyy') = dt_cardapio_p
	AND	((d.nr_Seq_servico = nr_seq_servico_p) OR (nr_seq_servico_p = 0));


	ds_retorno_w := vl_custo_w / qt_pessoa_atend_real_w;



end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
