CREATE OR REPLACE FUNCTION nut_obter_dados_genero_alim ( nr_seq_genero_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(30);
cd_unidade_medida_w	nut_genero_alim.cd_unidade_medida%type;


BEGIN
if (nr_seq_genero_p IS NOT NULL AND nr_seq_genero_p::text <> '') then

	select	max(cd_unidade_medida)
	into STRICT	cd_unidade_medida_w
	from	nut_genero_alim
	where	nr_sequencia = nr_seq_genero_p;

	if (ie_opcao_p = 'UN') then
		ds_retorno_w :=	cd_unidade_medida_w;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
