CREATE OR REPLACE FUNCTION nut_obter_dados_produto ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
utilizar ie_opção para customizações futuras
*/
ds_retorno_w		varchar(255);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '' AND ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then

	if (ie_opcao_p = 'DS') then
		select	ds_produto
		into STRICT 	ds_retorno_w
		from	nutricao_produto
		where 	nr_sequencia = nr_sequencia_p;
	end if;

end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
