CREATE OR REPLACE FUNCTION nut_obter_derivados_conduta (nr_seq_dieta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w            varchar(4000);
ds_derivado_w           varchar(255);
qt_porcentagem_w        double precision;
qt_dose_w               double precision;
cd_unidade_medida_w     varchar(30);

c01 CURSOR FOR
	SELECT  distinct b.ds_material ds_derivado,
		a.qt_porcentagem,
		a.qt_dose,
		a.cd_unidade_medida_dosa
	from    nut_atend_serv_dia_dieta a,
		material b
	where   a.cd_material = b.cd_material
	and     nr_sequencia_diluicao = nr_seq_dieta_p
	order by 1;

BEGIN

open C01;
loop
fetch C01 into
	ds_derivado_w,
	qt_porcentagem_w,
	qt_dose_w,
	cd_unidade_medida_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (qt_dose_w IS NOT NULL AND qt_dose_w::text <> '') then
		ds_retorno_w := SUBSTR(ds_retorno_w||ds_derivado_w||' ('||qt_dose_w||' '||cd_unidade_medida_w||'), ',1,4000);
	else
		ds_retorno_w := SUBSTR(ds_retorno_w||ds_derivado_w||' ('|| formata_casas_decimais(qt_porcentagem_w, 2)||'%), ',1,4000);
	end if;
	end;
end loop;
close C01;

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	ds_retorno_w := substr(ds_retorno_w,1,length(ds_retorno_w)-2);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
