CREATE OR REPLACE FUNCTION nut_obter_desc_composicao ( nr_seq_comp_p bigint) RETURNS varchar AS $body$
DECLARE


ds_composicao_w	varchar(255);

BEGIN

if (nr_seq_comp_p IS NOT NULL AND nr_seq_comp_p::text <> '') then
	select 	ds_composicao
	into STRICT	ds_composicao_w
	from	nut_composicao
	where	nr_sequencia	= nr_seq_comp_p;
end if;


return	ds_composicao_w;

end;
$body$
