CREATE OR REPLACE FUNCTION nut_obter_desc_comp_receita ( nr_seq_receita_p bigint) RETURNS varchar AS $body$
DECLARE


ds_composicao_w	varchar(100);


BEGIN
if (nr_seq_receita_p IS NOT NULL AND nr_seq_receita_p::text <> '') then

	SELECT  ds_Composicao
	INTO STRICT 	ds_composicao_w
	FROM	nut_composicao a,
		nut_receita b
	WHERE   a.nr_Sequencia = b.nr_seq_composicao
	AND	b.nr_sequencia = nr_seq_receita_p;

end if;

return	ds_composicao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
