CREATE OR REPLACE FUNCTION nut_obter_nome_acompanhante ( cd_acompanhante_p text, nr_seq_acompanhante_p bigint) RETURNS varchar AS $body$
DECLARE

 
nm_pessoa_fisica_w	varchar(80);

BEGIN
 
 
if (cd_acompanhante_p <> 0) then 
	select max(substr(obter_nome_pf(cd_pessoa_fisica),1,80)) 
	into STRICT	nm_pessoa_fisica_w 
	from	pessoa_fisica 
	where	cd_pessoa_fisica = cd_acompanhante_p;
else 
	select 	max(nm_pessoa_fisica) 
	into STRICT	nm_pessoa_fisica_w 
	from	nut_atend_acompanhante 
	where	nr_sequencia 	= nr_seq_acompanhante_p;
end if;
 
 
return	nm_pessoa_fisica_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
