CREATE OR REPLACE FUNCTION nut_obter_porcao_receita ( nr_seq_receita_comp_p bigint) RETURNS bigint AS $body$
DECLARE


qt_porcao_w	double precision;

BEGIN

if (nr_seq_receita_comp_p IS NOT NULL AND nr_seq_receita_comp_p::text <> '') then

	SELECT (b.qt_componente/coalesce(x.qt_rendimento,b.qt_componente))
	into STRICT	qt_porcao_w
	FROM 	nut_receita_comp b,
		nut_receita x
	WHERE	x.nr_sequencia = b.nr_seq_receita
	AND	b.nr_sequencia = nr_seq_receita_comp_p;
end if;

return	qt_porcao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
