CREATE OR REPLACE FUNCTION nut_obter_prescr_conduta_susp ( nr_prescricao_p bigint, nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_prescricao_w		prescr_medica.nr_prescricao%type;


BEGIN

select	max(b.nr_prescricao)
into STRICT	nr_prescricao_w
from	nut_atend_serv_dia a,
		prescr_medica b
where	a.nr_atendimento = b.nr_atendimento
and		a.nr_atendimento = nr_atendimento_p
and (b.nr_prescricao = nr_prescricao_p or b.nr_prescricao	<> nr_prescricao_p)
and		coalesce(b.dt_suspensao::text, '') = ''
AND		(coalesce(b.dt_liberacao, b.dt_liberacao_medico) IS NOT NULL AND (coalesce(b.dt_liberacao, b.dt_liberacao_medico))::text <> '')
and		((clock_timestamp() between PKG_DATE_UTILS.start_of(b.dt_inicio_prescr,'DAY') and PKG_DATE_UTILS.end_of(b.dt_validade_prescr, 'DAY')) or (coalesce(b.dt_validade_prescr::text, '') = ''))
AND 	((EXISTS (SELECT 1 FROM prescr_dieta x WHERE b.nr_prescricao = x.nr_prescricao and coalesce(x.ie_suspenso,'N') <> 'S'))
OR (EXISTS (SELECT 1 FROM rep_jejum x WHERE b.nr_prescricao = x.nr_prescricao and coalesce(x.ie_suspenso,'N') <> 'S'))
OR (EXISTS (SELECT 1 FROM prescr_material x WHERE b.nr_prescricao = x.nr_prescricao AND x.ie_agrupador IN (8,12) and coalesce(x.ie_suspenso,'N') <> 'S'))
OR (EXISTS (SELECT 1 FROM nut_pac x WHERE b.nr_prescricao = x.nr_prescricao and coalesce(x.ie_suspenso,'N') <> 'S'))
OR (EXISTS (SELECT 1 FROM prescr_leite_deriv x WHERE b.nr_prescricao = x.nr_prescricao)));

return	nr_prescricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
