DECLARE

 
			 
qt_retorno_w	integer;			
			 

BEGIN 
 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
 
	select 	count(*) 
	into STRICT	qt_retorno_w 
	from 	eis_gestao_nutricao_v a, 
		NUT_ATEND_ACOMPANHANTE b 
	where 	b.NR_SEQ_ATEND_SERV_DIA = a.nr_sequencia 
