CREATE OR REPLACE FUNCTION nut_obter_quantidade_servico ( cd_setor_atendimento_p bigint, dt_inicio_p timestamp, dt_final_p timestamp) RETURNS bigint AS $body$
DECLARE


quant_servico_w		bigint := 0;


BEGIN
if (coalesce(cd_setor_atendimento_p,0) > 0) then
	select	 count(*)
	into STRICT	 quant_servico_w
	from	 nut_atend_serv_dia a
	where	 cd_setor_atendimento = cd_setor_atendimento_p
	and	 dt_servico between trunc(dt_inicio_p) and fim_dia(dt_final_p)
	and	 coalesce(dt_liberacao::text, '') = ''
	and      substr(nut_obter_se_tem_jejum(a.nr_sequencia), 1, 1) = 'N';
end if;

return	quant_servico_w;

end;
