SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION nut_obter_receita_concat ( nr_seq_comp_p bigint, nr_seq_dieta_p bigint, nr_seq_grupo_p bigint, nr_servico_p bigint, nr_local_p bigint, nr_seq_opcao_p bigint, ie_trazer_quantidade_p text, dt_referencia_p timestamp ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);
ds_receita_w		varchar(255);
ie_dia_semana_w		smallint;


C01 CURSOR FOR
	SELECT DISTINCT
		a.ds_receita ds_receita
	FROM 	nut_receita a,
		nut_cardapio_dia b,
		nut_cardapio e
	WHERE 	e.nr_seq_card_dia	= b.nr_sequencia
	AND	a.nr_sequencia		= e.nr_seq_receita
	AND 	'N' = ie_trazer_quantidade_p
	AND	e.nr_seq_comp		= nr_seq_comp_p
	AND	coalesce(b.cd_dieta, 0)	= nr_seq_dieta_p
	AND	coalesce(b.nr_seq_grupo_producao, 0) = nr_seq_grupo_p
	AND	coalesce(b.nr_seq_servico, 0) = nr_servico_p
	AND	coalesce(b.nr_seq_local, 0)	= nr_local_p
	AND	obter_semana_cardapio(dt_referencia_p) = b.ie_semana
	AND	dt_vigencia_inicial	>= obter_dia_ini_fim_vig(dt_referencia_p,'I')
	AND	dt_vigencia_final	<= obter_dia_ini_fim_vig(dt_referencia_p,'F')
	AND	coalesce(b.nr_seq_opcao, 0) = nr_seq_opcao_p
	AND	coalesce(b.dt_cardapio::text, '') = ''
	and	b.ie_dia_semana = ie_dia_semana_w
	
UNION

	SELECT DISTINCT a.ds_receita || CASE WHEN coalesce(e.qt_refeicao::text, '') = '' THEN  ''  ELSE ' ('||e.qt_refeicao||') ' END  ds_receita
	FROM 	nut_receita a,
		nut_cardapio_dia b,
		nut_cardapio e
	WHERE 	e.nr_seq_card_dia	= b.nr_sequencia
	AND	a.nr_sequencia		= e.nr_seq_receita
	AND 	'S' = ie_trazer_quantidade_p
	AND	e.nr_seq_comp		= nr_seq_comp_p
	AND	coalesce(b.cd_dieta, 0)	= nr_seq_dieta_p
	AND	coalesce(b.nr_seq_grupo_producao, 0) = nr_seq_grupo_p
	AND	coalesce(b.nr_seq_servico, 0) = nr_servico_p
	AND	coalesce(b.nr_seq_local, 0)	= nr_local_p
	AND	obter_semana_cardapio(dt_referencia_p) = b.ie_semana
	AND	dt_vigencia_inicial	>= obter_dia_ini_fim_vig(dt_referencia_p,'I')
	AND	dt_vigencia_final	<= obter_dia_ini_fim_vig(dt_referencia_p,'F')
	AND	coalesce(b.nr_seq_opcao, 0) = nr_seq_opcao_p
	AND	coalesce(b.dt_cardapio::text, '') = ''
	and	b.ie_dia_semana = ie_dia_semana_w;


BEGIN

ie_dia_semana_w := pkg_date_utils.get_weekday(dt_referencia_p);

open C01;
loop
fetch C01 into
	ds_receita_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_retorno_w := substr(ds_retorno_w || ds_receita_w || chr(13) || chr(10),1,2000);

	end;
end loop;
close C01;

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	ds_retorno_w := substr( ds_retorno_w ,1,length( ds_retorno_w )-1);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
