CREATE OR REPLACE FUNCTION nut_obter_receita_subst ( cd_pessoa_fisica_p text, nr_seq_servico_p bigint, nr_seq_receita_old_p bigint) RETURNS bigint AS $body$
DECLARE

			
nr_seq_receita_new_w	bigint;
nr_sequencia_w		nut_regra_subst_receita.nr_sequencia%type;


BEGIN

if (nr_seq_receita_old_p IS NOT NULL AND nr_seq_receita_old_p::text <> '') then

	select	max(a.nr_sequencia)
	into STRICT	nr_sequencia_w
	from	nut_regra_subst_receita a
	where	a.nr_seq_servico = nr_seq_servico_p
	and	a.nr_seq_receita_old = nr_seq_receita_old_p
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	clock_timestamp() between COALESCE(a.dt_inicio, clock_timestamp()) and COALESCE(a.dt_fim, clock_timestamp());
	
	if (coalesce(nr_sequencia_w::text, '') = '') then
	
		select	max(a.nr_sequencia)
		into STRICT	nr_sequencia_w
		from	nut_regra_subst_receita a
		where	coalesce(a.nr_seq_servico::text, '') = ''
		and	a.nr_seq_receita_old = nr_seq_receita_old_p
		and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
		and	clock_timestamp() between COALESCE(a.dt_inicio, clock_timestamp()) and COALESCE(a.dt_fim, clock_timestamp());
	
	end if;
	
	select	max(a.nr_seq_receita_new)
	into STRICT	nr_seq_receita_new_w
	from	nut_regra_subst_receita a
	where	a.nr_sequencia = nr_sequencia_w;

end if;

return	nr_seq_receita_new_w;

end;
$body$
