CREATE OR REPLACE FUNCTION nut_obter_se_card_nao_lib ( nr_atendimento_p bigint, dt_referencia_p timestamp, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1);
			

BEGIN


SELECT	coalesce(MAX('S'), 'N')
into STRICT	ds_retorno_w
FROM	nut_pac_opcao_rec a,
	nut_atend_serv_dia b
WHERE	a.nr_seq_servico_dia		= b.nr_sequencia
AND	b.nr_atendimento		= nr_atendimento_p
AND	PKG_DATE_UTILS.start_of(b.DT_SERVICO, 'DAY')		= PKG_DATE_UTILS.start_of(dt_referencia_p, 'DAY')	
and	coalesce(a.dt_liberacao::text, '') = '';


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
