CREATE OR REPLACE FUNCTION nut_obter_se_check_cad_padrao ( nr_atendimento_p bigint, nr_seq_servico_p bigint, cd_setor_atendimento_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_cor_w	varchar(15);
ds_retorno_w	varchar(1);


BEGIN

select 	nut_obter_cor_cardapio_padrao(b.nr_prescr_oral,dt_referencia_p,nr_seq_servico_p,cd_setor_atendimento_p,nr_atendimento_p)
into STRICT	ds_cor_w
from	nut_atend_serv_dia_rep b,
	nut_atend_serv_dia c
where	b.nr_seq_serv_dia   	= c.nr_sequencia
and	c.nr_atendimento 	= nr_atendimento_p
and	c.nr_seq_servico 	= nr_seq_servico_p
and	c.cd_setor_atendimento 	= cd_setor_atendimento_p
and	c.dt_servico between inicio_dia(dt_referencia_p) and fim_dia(dt_referencia_p)
and	(c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> '')
and	not exists ( SELECT 1
		     from   nut_pac_opcao_rec x
		     where  x.nr_seq_servico_dia = c.nr_sequencia);

if (ds_cor_w IS NOT NULL AND ds_cor_w::text <> '') then
	ds_retorno_w	:= 'S';
end if;



return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
