CREATE OR REPLACE FUNCTION nut_obter_se_existe_acomp ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint, ds_servicos_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1);
nr_seq_servico_lista_w	varchar(2000);
nr_seq_servico_w	bigint;
contador_w		bigint;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	nr_seq_servico_lista_w	:= ds_servicos_p;
	contador_w := 1;
	while (nr_seq_servico_lista_w IS NOT NULL AND nr_seq_servico_lista_w::text <> '' AND contador_w < 100) loop

		nr_seq_servico_w		:= substr(nr_seq_servico_lista_w, 1, position(',' in nr_seq_servico_lista_w) - 1);
		nr_seq_servico_lista_w		:= substr(nr_seq_servico_lista_w, position(',' in nr_seq_servico_lista_w) + 1, length(nr_seq_servico_lista_w));


		SELECT	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_retorno_w
		FROM   	nut_atend_serv_dia a,
			nut_atend_acompanhante b
		WHERE  	a.nr_sequencia 		  =  b.nr_seq_atend_serv_dia
		AND	a.nr_atendimento	  =  nr_atendimento_p
		AND	a.cd_setor_atendimento	  =  cd_setor_atendimento_p
		AND	a.nr_seq_servico 	  =  nr_seq_servico_w;

		contador_w:= contador_w + 1;
		if (ie_retorno_w = 'S') then
			nr_seq_servico_lista_w := null;
		end if;
	end loop;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
