SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION nut_obter_se_grupo_producao ( nr_seq_grupo_producao_p bigint, nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
ie_existe_dieta_w	varchar(1);
ie_grupo_todas_dietas_w	varchar(1);
cd_dieta_w		bigint;

/*
ie_opcao_p
P - Prescrição
A - Acompanhante
C - Conduta Dietoteapica
*/
C01 CURSOR FOR
	SELECT	cd_dieta
	from	prescr_dieta
	where	nr_prescricao = nr_sequencia_p
	and	ie_opcao_p = 'P'
	
union

	SELECT	cd_dieta
	from	nut_atend_acomp_dieta
	where	nr_seq_atend_acomp = nr_sequencia_p
	and	ie_opcao_p = 'A'
	
union

	select	a.cd_dieta
	from	nut_atend_serv_dia_dieta a,
		nut_atend_serv_dieta b
	where	a.nr_sequencia = b.nr_seq_dieta
	and	b.nr_seq_servico = nr_sequencia_p
	and	(a.cd_dieta IS NOT NULL AND a.cd_dieta::text <> '')
	and	coalesce(a.dt_suspensao::text, '') = ''
	and	ie_opcao_p = 'C';


BEGIN

if (nr_seq_grupo_producao_p IS NOT NULL AND nr_seq_grupo_producao_p::text <> '') then

	ie_grupo_todas_dietas_w := obter_valor_param_usuario(1003, 80, Obter_perfil_Ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento);
	ds_retorno_w		:= ie_grupo_todas_dietas_w;

	open C01;
	loop
	fetch C01 into
		cd_dieta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		begin
		select	'S'
		into STRICT	ie_existe_dieta_w
		from	nut_grupo_producao_dieta
		where	nr_seq_grupo_producao = nr_seq_grupo_producao_p
		and	cd_dieta = cd_dieta_w  LIMIT 1;
		exception
		when others then
			ie_existe_dieta_w := 'N';
		end;

		if (ie_grupo_todas_dietas_w = 'S') and (ie_existe_dieta_w = 'N') then
			ds_retorno_w := 'N';
		elsif (ie_grupo_todas_dietas_w = 'N') and (ie_existe_dieta_w = 'S') then
			ds_retorno_w := 'S';
		end if;

		end;
	end loop;
	close C01;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
