CREATE OR REPLACE FUNCTION nut_obter_se_horario_no_turno ( nr_seq_turno_p bigint, dt_horario_p timestamp) RETURNS varchar AS $body$
DECLARE


hr_inicial_w		timestamp;
hr_final_w		timestamp;
dt_inicio_turno_w	timestamp;
dt_fim_turno_w		timestamp;
ds_retorno_w		varchar(1);


BEGIN

select	hr_inicial,
	hr_final
into STRICT	hr_inicial_w,
	hr_final_w
from	nut_turno_lactario
where	nr_sequencia	= nr_seq_turno_p;

dt_inicio_turno_w	:=	to_date(to_char(dt_horario_p,'dd/mm/yyyy')||' ' || to_char(hr_inicial_w,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss');
dt_fim_turno_w		:=	to_date(to_char(dt_horario_p,'dd/mm/yyyy')||' ' || to_char(hr_final_w,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss');

if ((dt_horario_p >= dt_inicio_turno_w) and (dt_horario_p <= dt_fim_turno_w) and (dt_fim_turno_w > dt_inicio_turno_w)) or
   (((dt_horario_p >= dt_inicio_turno_w) or (dt_horario_p <= dt_fim_turno_w)) and (dt_fim_turno_w < dt_inicio_turno_w)) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
