CREATE OR REPLACE FUNCTION nut_obter_se_local_composicao ( nr_seq_local_p bigint) RETURNS varchar AS $body$
DECLARE


qt_valida_w	bigint;
ds_retorno_w	varchar(1);


BEGIN
ds_retorno_w := 'N';
if (nr_seq_local_p IS NOT NULL AND nr_seq_local_p::text <> '') then
	select	count(*)
	into STRICT	qt_valida_w
	from	nut_local_refeicao
	where	nr_sequencia = nr_seq_local_p
	and	coalesce(ie_local_paciente,'N') = 'C';

	if (qt_valida_w > 0 ) then
		ds_retorno_w := 'S';
	end if;


end if;


return	 ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
