SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION nut_obter_se_nova_prescr ( nr_seq_mapa_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
nr_atendimento_w	bigint;
dt_liberacao_dieta_w	timestamp;


BEGIN

if (nr_seq_mapa_p IS NOT NULL AND nr_seq_mapa_p::text <> '') then

	select	max(nr_atendimento),
		max(dt_liberacao)
	into STRICT	nr_atendimento_w,
		dt_liberacao_dieta_w
	from	mapa_dieta
	where	nr_sequencia = nr_seq_mapa_p;

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	prescr_medica a
	where 	a.nr_atendimento = nr_atendimento_w
	and 	a.dt_prescricao	> dt_liberacao_dieta_w
	and	a.dt_validade_prescr > clock_timestamp()
	and (exists (SELECT	1
			from	prescr_dieta b
			where	a.nr_prescricao = b.nr_prescricao)
	or	exists (	select	1
			from	prescr_material b
			where	a.nr_prescricao = b.nr_prescricao
			and	b.ie_agrupador in (8,12)));
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION nut_obter_se_nova_prescr ( nr_seq_mapa_p bigint) FROM PUBLIC;

