CREATE OR REPLACE FUNCTION nut_obter_se_possui_producao ( cd_setor_prescricao_p bigint, nr_seq_producao_p bigint default 0) RETURNS varchar AS $body$
DECLARE


ie_possui_producao_w varchar(10) := 'S';


BEGIN

if (cd_setor_prescricao_p IS NOT NULL AND cd_setor_prescricao_p::text <> '') then
	select  CASE WHEN sum(qt + qt_1)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_possui_producao_w
	from	(SELECT  count(*) qt
		FROM prescr_medica i, prescr_material g, material c, nut_producao_lactario_item a
LEFT OUTER JOIN prescr_mat_hor b ON (a.nr_seq_mat_hor = b.nr_sequencia)
WHERE a.cd_material = c.cd_material and (a.nr_seq_prod_lac = nr_seq_producao_p or nr_seq_producao_p = 0) and g.nr_prescricao = a.nr_prescricao and g.nr_sequencia = a.nr_seq_prescr and i.nr_prescricao = g.nr_prescricao and ((i.cd_setor_atendimento = cd_setor_prescricao_p) or ( cd_setor_prescricao_p = '0')) and coalesce(a.ie_suspenso,'N') <> 'S' and not exists (select 1 from nut_producao_lac_item_adic d where a.nr_sequencia = d.nr_seq_prod_item) and exists (select 1 from atendimento_paciente x where x.nr_atendimento = a.nr_atendimento and x.cd_estabelecimento = coalesce(wheb_usuario_pck.get_cd_estabelecimento,x.cd_estabelecimento)) ) a,
		(select  count(*) qt_1
		FROM prescr_medica i, prescr_material g, material c, nut_producao_lactario_item a
LEFT OUTER JOIN prescr_mat_hor b ON (a.nr_seq_mat_hor = b.nr_sequencia)
WHERE a.cd_material = c.cd_material and g.nr_prescricao = a.nr_prescricao and g.nr_sequencia = a.nr_seq_prescr and i.nr_prescricao = g.nr_prescricao and ((i.cd_setor_atendimento = cd_setor_prescricao_p) or ( cd_setor_prescricao_p = '0')) and coalesce(a.ie_suspenso,'N') <> 'S' and (a.nr_seq_prod_lac = nr_seq_producao_p or nr_seq_producao_p = 0) and exists (select 1 from nut_producao_lac_item_adic d where a.nr_sequencia = d.nr_seq_prod_item) and exists (select 1 from atendimento_paciente x where x.nr_atendimento = a.nr_atendimento and x.cd_estabelecimento = coalesce(wheb_usuario_pck.get_cd_estabelecimento,x.cd_estabelecimento)) ) b;
end if;

return	ie_possui_producao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
