CREATE OR REPLACE FUNCTION nut_obter_se_prescr_avaliar ( dt_referencia_inicial_p timestamp, dt_referencia_final_p timestamp, nr_atendimento_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';


BEGIN

if (dt_referencia_inicial_p IS NOT NULL AND dt_referencia_inicial_p::text <> '') or (dt_referencia_final_p IS NOT NULL AND dt_referencia_final_p::text <> '') or (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') or (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	nut_atend_serv_dia a,
		prescr_medica b,
		nut_atend_serv_prescr_aval c
	where	((a.dt_servico between b.dt_inicio_prescr and b.dt_validade_prescr) or (coalesce(b.dt_validade_prescr::text, '') = ''))
	and	a.nr_sequencia = c.nr_seq_serv_dia
	and	b.nr_prescricao = c.nr_prescricao
	and 	a.nr_atendimento = b.nr_atendimento
	and	(coalesce(b.dt_liberacao, b.dt_liberacao_medico) IS NOT NULL AND (coalesce(b.dt_liberacao, b.dt_liberacao_medico))::text <> '')
	and	not exists (SELECT 1 from nut_atend_serv_dia_rep d where a.nr_sequencia = d.nr_seq_serv_dia and (coalesce(d.nr_prescr_oral,d.nr_prescr_jejum,d.nr_prescr_compl,d.nr_prescr_enteral,d.nr_prescr_npt_adulta,d.nr_prescr_npt_ped,d.nr_prescr_npt_neo, d.nr_prescr_leite_deriv) IS NOT NULL AND (coalesce(d.nr_prescr_oral,d.nr_prescr_jejum,d.nr_prescr_compl,d.nr_prescr_enteral,d.nr_prescr_npt_adulta,d.nr_prescr_npt_ped,d.nr_prescr_npt_neo, d.nr_prescr_leite_deriv))::text <> ''))
	and	((a.cd_setor_atendimento = cd_setor_atendimento_p) or (coalesce(cd_setor_atendimento_p::text, '') = ''))
	and	a.nr_atendimento = nr_atendimento_p
	and	a.dt_servico between dt_referencia_inicial_p and dt_referencia_final_p;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
