SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION nut_obter_se_prescr_item (nr_prescricao_p bigint) RETURNS char AS $body$
DECLARE


ie_retorno_w	char(1) := 'N';


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	prescr_medica b
	where	b.nr_prescricao = nr_prescricao_p;

	if (ie_retorno_w = 'N') then
		select	coalesce(max('S'),'N')
		into STRICT	ie_retorno_w
		from	prescr_dieta b where		b.nr_prescricao = nr_prescricao_p
		and		coalesce(b.ie_suspenso,'N') = 'N' LIMIT 1;

		if (ie_retorno_w = 'N') then
			select	coalesce(max('S'),'N')
			into STRICT	ie_retorno_w
			from	rep_jejum b where		b.nr_prescricao = nr_prescricao_p
			and		coalesce(b.ie_suspenso,'N') = 'N' LIMIT 1;

			if (ie_retorno_w = 'N') then
				select	coalesce(max('S'),'N')
				into STRICT	ie_retorno_w
				from	prescr_material b where		b.nr_prescricao = nr_prescricao_p
				and		b.ie_agrupador in (8,12)
				and		coalesce(b.ie_suspenso,'N') = 'N' LIMIT 1;

				if (ie_retorno_w = 'N') then
					select	coalesce(max('S'),'N')
					into STRICT	ie_retorno_w
					from	nut_pac b where		b.nr_prescricao = nr_prescricao_p
					and		coalesce(b.ie_suspenso,'N') = 'N' LIMIT 1;

					if (ie_retorno_w = 'N') then
						select	coalesce(max('S'),'N')
						into STRICT	ie_retorno_w
						from	prescr_leite_deriv b where		b.nr_prescricao = nr_prescricao_p LIMIT 1;
					end if;
				end if;
			end if;
		end if;
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
