CREATE OR REPLACE FUNCTION nut_obter_se_serv_bloq_adep ( dt_servico_p timestamp, cd_setor_atendimento_p bigint, nr_atendimento_p bigint, nr_Seq_Servico_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1);
ie_utiliza_servico_adep_w	varchar(1);


BEGIN

ie_utiliza_servico_adep_w := obter_param_usuario(1113, 309, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_utiliza_servico_adep_w);

if (ie_utiliza_servico_adep_w = 'N') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	nut_Atend_Serv_dia a,
		nut_Atend_serv_dia_rep b,
		prescr_dieta_hor c
	where	a.nr_sequencia = b.nr_seq_serv_dia
	and	a.dt_servico = dt_servico_p
	and	a.cd_setor_Atendimento = cd_setor_Atendimento_p
	and	a.nr_atendimento = nr_Atendimento_p
	and	b.nr_prescr_oral = c.nr_prescricao
	and	to_char(a.dt_servico,'dd/mm/yyyy hh24:mi') = to_char(c.dt_horario,'dd/mm/yyyy hh24:mi')
	and	(c.dt_bloqueio IS NOT NULL AND c.dt_bloqueio::text <> '')
	and	a.nr_Seq_Servico = nr_Seq_Servico_p;

elsif (ie_utiliza_servico_adep_w = 'S') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	nut_Atend_Serv_dia a
	where	a.dt_servico = dt_servico_p
	and		a.cd_setor_Atendimento = cd_setor_Atendimento_p
	and		a.nr_atendimento = nr_Atendimento_p
	and		(a.dt_bloqueio IS NOT NULL AND a.dt_bloqueio::text <> '')
	and		a.nr_Seq_Servico = nr_Seq_Servico_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
