CREATE OR REPLACE FUNCTION nut_obter_se_tem_jejum_atx (nr_seq_nut_atend_serv_dia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';
BEGIN

if (nr_seq_nut_atend_serv_dia_p IS NOT NULL AND nr_seq_nut_atend_serv_dia_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from  	nut_atend_serv_dia a,
		prescr_medica b,
		rep_jejum c
	where	a.nr_atendimento = b.nr_atendimento
	and     b.nr_prescricao = c.nr_prescricao
	and	(coalesce(b.dt_liberacao, b.dt_liberacao_medico) IS NOT NULL AND (coalesce(b.dt_liberacao, b.dt_liberacao_medico))::text <> '')
	and	a.dt_servico between b.dt_inicio_prescr and b.dt_validade_prescr
	and	a.dt_servico between c.dt_inicio and c.dt_fim
	and	GN_obter_se_itens_susp(b.nr_prescricao) = 'N'
	and	a.nr_sequencia = nr_seq_nut_atend_serv_dia_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
