CREATE OR REPLACE FUNCTION nut_obter_status_pac (nr_atendimento_p bigint, cd_setor_atendimento_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_status_pac_w		varchar(05);
dt_inicial_w		timestamp;
dt_final_w		timestamp;


BEGIN

dt_inicial_w		:= trunc(dt_referencia_p, 'dd');
dt_final_w		:= trunc(dt_referencia_p, 'dd') + 86399/86400;


select	coalesce(max(ie_status_prescricao), 'SP')
into STRICT	ie_status_pac_w
from	nut_prescricao a
where	a.nr_atendimento	= nr_atendimento_p
and	a.cd_setor_atendimento	= cd_setor_atendimento_p
and	a.dt_prescricao between dt_inicial_w and dt_final_w;

return	ie_status_pac_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
